package com.qst.goldenarches.controller;import com.qst.goldenarches.pojo.VIP;import com.qst.goldenarches.service.VIPService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@Controllerpublic class VIPController {    @Autowired    private VIPService vipService;    @RequestMapping(value = "/addForm", method = RequestMethod.POST)    public String add(@ModelAttribute VIP vip, Model model){        vipService.addVIP(vip);        model.addAttribute("vip", vip);        return "forward:/balance.jsp";    }    @RequestMapping(value = "/showBalance", method = RequestMethod.POST)    public String find(@ModelAttribute VIP vip, Model model){        vip = vipService.selBalance(vip.getPhone());        model.addAttribute("vip", vip);        return "forward:/balance.jsp";    }    @RequestMapping("/update")    public String update(String phone, String balance){        vipService.updBalance(phone, Double.parseDouble(balance));        return "forward:member/index";    }}