/** * FileName: RoleService * Author:   SAMSUNG-PC 孙中军 * Date:     2018/9/27 17:59 * Description: */package com.qst.goldenarches.service;import com.qst.goldenarches.pojo.Role;import java.util.List;import java.util.Map;public interface RoleService {    /**     * 获取所有符合条件的角色     * @param queryText 查询条件 可以为null     * @return     */    List<Role> getAllRoles(String queryText);    /**     * 为角色分配许可菜单     * @param paramMap     */    void addRolePermissions(Map<String, Object> paramMap);    /**     * 增加角色     * @param role     * @return     */    void addRole(Role role);    /**     * 修改角色名字     * @param role     */    void editRole(Role role);    /**     * 检查数据库中是否有该角色的名字     * @param name     * @return     */    boolean validateNameUnique(String name);    /**     * 获取角色     * @param id     * @return     */    Role getRoleById(String id);    /**     * 删除角色     * 同时会删除和这个角色相关的内容     * 删除admin_role &role_permission内容     * @param map     */    void removeRoles(Map<String, Object> map);}